                                            <script type="text/javascript">
                                                    $(document).ready(function() {
                                                        owl = $('.owl-gal');
                                                        owl.owlCarousel({
                                                            'items': 3,
                                                            'dots': true
                                                        });

                                                        $(document).on('click', '.owl-gal .owl-gal-img', function() {
                                                            $('.owl-gal .owl-gal-img').removeClass('clicked');
                                                            $(this).addClass('clicked');
                                                            var src = $(this).data('src');
                                                            $('.main-owl-img').attr('src', src);
                                                            $('.main-owl-img').parent().attr('href', src);
                                                        });

                                                        $(document).on('click', '.owl-nav', function() {
                                                            if ($(this).data('move')=='prev') {
                                                                var curImg = $('.owl-gal .owl-gal-img.clicked').parent();   
                                                                if ($(curImg).prev().length>0)
                                                                    $('.owl-gal .owl-gal-img').removeClass('clicked');
                                                                $(curImg).prev().find('.owl-gal-img').addClass('clicked');
                                                                $('.main-owl-img').attr('src', $(curImg).prev().find('.owl-gal-img').data('src'));
                                                                $('.main-owl-img').parent().attr('href', $(curImg).prev().find('.owl-gal-img').data('src'));
                                                                if ($(curImg).next().hasClass('active') && $(curImg).prev().hasClass('active') || $(curImg).next().hasClass('active') && $(curImg).next().next().hasClass('active'))
                                                                    owl.trigger('prev.owl');

                                                            } else if ($(this).data('move')=='next') {
                                                                var curImg = $('.owl-gal .owl-gal-img.clicked').parent();
                                                                if ($(curImg).next().length>0)
                                                                    $('.owl-gal .owl-gal-img').removeClass('clicked');
                                                                $(curImg).next().find('.owl-gal-img').addClass('clicked');
                                                                $('.main-owl-img').attr('src', $(curImg).next().find('.owl-gal-img').data('src'));
                                                                $('.main-owl-img').parent().attr('href', $(curImg).next().find('.owl-gal-img').data('src'));
                                                                if ($(curImg).next().hasClass('active') && $(curImg).prev().hasClass('active') || $(curImg).prev().hasClass('active') && $(curImg).prev().prev().hasClass('active'))
